mapper.select:
select
  id, account_id, goods_id, scene, order_sn, pay_status, status, refund_status, created, pay_time, send_time, cencel_time, delete_time
from sblog.orders


        and id = #{id}


        and account_id = #{accountId}


        and goods_id = #{goodsId}


        and scene = #{scene}


        and order_sn = #{orderSn}


        and pay_status = #{payStatus}


        and status = #{status}


        and refund_status = #{refundStatus}


        and created = #{created}


        and pay_time = #{payTime}


        and send_time = #{sendTime}


        and cencel_time = #{cencelTime}


        and delete_time = #{deleteTime}
